load('ex3weights.mat');theta2File = fopen('Theta2.txt','w');for k = 1:size(Theta2,1)  for i = 1:size(Theta2,2)      if(i == 1)        fprintf(theta2File,'{ %d,',Theta2(k,i));      elseif(i == size(Theta2,2))        disp(1);        fprintf(theta2File,'%d},\n',Theta2(k,i));      else        fprintf(theta2File,'%d, ',Theta2(k,i));      end  endendfclose('Theta2.txt');