load('ex3weights.mat');theta1File = fopen('Theta1.txt','w');for k = 1:size(Theta1,1)  for i = 1:size(Theta1,2)      if(i == 1)        fprintf(theta1File,'{ %d,',Theta1(k,i));      elseif(i == size(Theta1,2))        disp(1);        fprintf(theta1File,'%d},\n',Theta1(k,i));      else        fprintf(theta1File,'%d, ',Theta1(k,i));      end  endendfclose('Theta1.txt');